mapper.update:
UPDATE store
SET seat_status = seat_status + #{numGuests}
WHERE store_seq = #{storeSeq}

UPDATE store
SET seat_status = seat_status - #{numGuests}
WHERE store_seq = #{storeSeq}

mapper.insert:
INSERT INTO cancellation_records (
cancellation_id, user_id, cancellation_time
) VALUES (
cancellation_id_seq.NEXTVAL, #{userId}, #{cancellationTime}
)

mapper.update:
UPDATE reservation
SET status = #{status}
WHERE reservation_id = #{reservationId}
